<?php

class Stripe_Express_Menu
{
    function register_menu()
    {
        $svg_img = 'data:image/svg+xml;base64,';
        add_menu_page(
            'Stripe Express',
            'Stripe Express',
            'manage_options',
            'stripe-express',
            array( $this, 'admin_page' ),
            $svg_img,
            58
        );
        add_submenu_page(
            'stripe-express',
            'One-Click Import Demo',
            'Import Demo',
            'manage_options',
            'admin.php?page=stripe-express#/demo'
        );
    }
    
    function admin_page()
    {
        wp_enqueue_style( 'wp-stripe-express-admin' );
        wp_enqueue_script( 'wp-stripe-express-admin' );
        wp_localize_script( 'wp-stripe-express-admin', 'wp_stripe_setting', array(
            'api_url'  => rest_url( IT_STRIPE_EXPRESS_REST_API . 'admin' ),
            'nonce'    => wp_create_nonce( 'wp_rest' ),
            'role'     => $this->get_role(),
            'language' => get_locale(),
        ) );
        echo  '<div id="wp_stripe_express_admin"></div>' ;
    }
    
    private function get_role()
    {
        return 1;
    }

}